const fs = require('fs');
const path = require('path');

const fileContentBuffer = fs.readFileSync(
  path.resolve(
    __dirname,
    './content.txt'
  )
);

const fileContentEncoded = fs.readFileSync(
  path.resolve(
    __dirname,
    './content.txt'
  ),
  'utf8'
);

module.exports = {
  contentBuffer: fileContentBuffer,
  contentEncoded: fileContentEncoded
};
